function device_filter(){for(var e=document.getElementById("filterInput").value.toLowerCase(),t=document.getElementById("device-list").getElementsByTagName("tr"),n=0;n<t.length;n++){var l=t[n].getElementsByTagName("td")[3];if(l)l.innerText.trim().replace(/\s+/g," ").toLowerCase().indexOf(e)>-1?t[n].style.display="":t[n].style.display="none"}}